import React from "react";

function handleEditProfileClick() {
  const popupEditProfile = document.querySelector(".popup_type_edit-profile");
  popupEditProfile.classList.add("popup_opened");
}

function handleEditAvatarClick() {
  const popupEditProfile = document.querySelector(".popup_type_update-avatar");
  popupEditProfile.classList.add("popup_opened");
}

function handleAddPlaceClick() {
  const popupEditProfile = document.querySelector(".popup_type_add-author");
  popupEditProfile.classList.add("popup_opened");
}

function Main() {
  // const [isOpenedPopup, setIsOpenedPopup] = React.useState(false);

  return (
    <>
      <main>
        <section className="profile">
          <div className="profile__section">
            <button
              className="profile__button-edit-avatar"
              type="button"
              onClick={() => {
                handleEditAvatarClick();
              }}
            >
              <img src="" alt="аватар" className="profile__avatar" />
            </button>
            <div className="profile__info">
              <div className="profile__author-block">
                <h2 className="profile__author">Oleg Klyuev</h2>
                <button
                  type="button"
                  className="profile__button-edit"
                  onClick={() => {
                    handleEditProfileClick();
                  }}
                ></button>
              </div>
              <p className="profile__job">Rider</p>
            </div>
          </div>
          <button
            className="profile__add-button"
            type="button"
            onClick={() => {
              handleAddPlaceClick();
            }}
          ></button>
        </section>

        <section className="elements">
          <template id="elementTemplate">
            <article className="element">
              <button className="element__button-photo" type="button">
                <img className="element__photo" />
              </button>
              <div className="element__place">
                <h3 className="element__place-name"></h3>
                <button className="element__delete" type="button"></button>
                <div className="element__likes">
                  <button className="element__like" type="button"></button>
                  <p className="element__number-like">1</p>
                </div>
              </div>
            </article>
          </template>
        </section>
      </main>
      <div className="popup popup_type_open-card">
        <div className="popup__card">
          <img className="popup__photo" />
          <h3 className="popup__place-name"></h3>
          <button
            className="popup__button-close popup__button-close_type_open-card"
            type="button"
          ></button>
        </div>
      </div>
    </>
  );
}

export default Main;
