import React from "react";

function handleEditAvatarClick() {
  const popupEditProfileForm = document.querySelector(
    ".popup__form[name=profile]"
  );
  popupEditProfileForm.classList.add("popup_opened");
}

function Main() {
  const [isOpenedPopup, setIsOpenedPopup] = React.useState(false);

  return (
    <main>
      <section className="profile">
        <div className="profile__section">
          <button className="profile__button-edit-avatar" type="button">
            <img src="" alt="аватар" className="profile__avatar" />
          </button>
          <div className="profile__info">
            <div className="profile__author-block">
              <h2 className="profile__author">Oleg Klyuev</h2>
              <button
                type="button"
                className="profile__button-edit"
                onClick={() => {
                  setIsOpenedPopup(true);
                  console.log(isOpenedPopup)
                  handleEditAvatarClick();
                }}
              ></button>
            </div>
            <p className="profile__job">megamozg</p>
          </div>
        </div>
        <button className="profile__add-button" type="button"></button>
      </section>

      <section className="elements">
        <template id="elementTemplate">
          <article className="element">
            <button className="element__button-photo" type="button">
              <img className="element__photo" />
            </button>
            <div className="element__place">
              <h3 className="element__place-name"></h3>
              <button className="element__delete" type="button"></button>
              <div className="element__likes">
                <button className="element__like" type="button"></button>
                <p className="element__number-like">1</p>
              </div>
            </div>
          </article>
        </template>
      </section>
    </main>
  );
}

export default Main;
