import React from "react";
import { Link, useLocation } from "react-router-dom";

function Header() {
  const location = useLocation();
  const [mobileEmail, setMobileEmail] = React.useState(false);
  const [mobileLink, setMobileLink] = React.useState(false);

  function handleBurgerClick() {
    setMobileEmail((state) => !state);
    setMobileLink((state) => !state);
  }

  window.addEventListener("resize", handleWindowWith);

  return (
    <header className="header">
      <div className="header__logo"></div>
      <div className="header__data">
        {location.pathname === "/"
            // <p
            //   className={`header__email_mobile  ${
            //     mobileEmail ? "header_mobile_opened" : ""
            //   }`}
            // ></p>
           && <p className="header__email">123@mail.ru</p>}
        
        {location.pathname === "/" && (
          <Link className="header__link" to="/sign-in">
            Выйти
          </Link>
        )}
        {location.pathname === "/" && (
          <button
            className="header__burger"
            onClick={handleBurgerClick}
          ></button>
        )}

        {location.pathname === "/sign-up" && (
          <Link className="header__link" to="/sign-in">
            Войти
          </Link>
        )}

        {location.pathname === "/sign-in" && (
          <Link className="header__link" to="/sign-up">
            Регистрация
          </Link>
        )}
      </div>
    </header>
  );
}

export default Header;
