import React from "react";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup";


function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true)
    // const popupEditProfile = document.querySelector(".popup_type_edit-profile");
    // popupEditProfile.classList.add("popup_opened");
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true)
    // const popupEditProfile = document.querySelector(".popup_type_update-avatar");
    // popupEditProfile.classList.add("popup_opened");
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true)
    // const popupEditProfile = document.querySelector(".popup_type_add-author");
    // popupEditProfile.classList.add("popup_opened");
  }

  return (
    <>
      <Header />
      <Main
      onEditProfile = {handleEditProfileClick}
      onAddPlace = {handleEditAvatarClick}
      onEditAvatar = {handleAddPlaceClick}
      />
      <Footer />

      <PopupWithForm
        name="edit-profile"
        title="Редактировать профиль"
        buttonName="Сохранить"
      >
        <label className="popup__label">
          <input
            name="author"
            type="text"
            className="popup__input"
            placeholder="Имя автора"
            // minlength="2"
            // maxlength="40"
            // required
            // novalidate
          />
          <span className="popup__input-error popup__input-error_type_author"></span>
        </label>
        <label className="popup__label">
          <input
            name="job"
            type="text"
            className="popup__input"
            placeholder="Занятие автора"
            // minlength="2"
            // maxlength="200"
            // required
            // novalidate
          />
          <span className="popup__input-error popup__input-error_type_job"></span>
        </label>
      </PopupWithForm>

      <PopupWithForm
      name ="update-avatar"
      title="Обновить аватар"
      buttonName="Сохранить"
      >
        <label className="popup__label popup__label_one">
              <input
                type="url"
                className="popup__input"
                name="avatar"
                placeholder="Адрес фотокарточки"
                required
              />
              <span className="popup__input-error popup__input-error_type_avatar">
              </span>
            </label>
      </PopupWithForm>

      <PopupWithForm
      name ="add-author"
      title="Новое место"
      buttonName="Создать"
      >
        <label className="popup__label">
              <input
                type="text"
                className="popup__input"
                name="place-name"
                placeholder="Название"
                // required
                // minlength="2"
                // maxlength="30"
                // novalidate
              />
              <span className="popup__input-error popup__input-error_type_place-name"></span>
            </label>

            <label className="popup__label">
              <input
                type="url"
                className="popup__input"
                name="photo"
                placeholder="Ссылка на картинку"
                required
              />
              <span className="popup__input-error popup__input-error_type_photo"></span>
            </label>

      </PopupWithForm>

      <PopupWithForm
      name="delete-card"
      title="Вы уверен?"
      buttonName="Да"
      >
      </PopupWithForm>

      <ImagePopup/>


    </>
  );
}

export default App;
