import React from "react";
import { api } from "../utils/api";

function Main() {
  const [userName, setUserName] = React.useState('')
  const [userDescription, setUserDescription] = React.useState('')
  const [userAvatar, setUserAvatar] = React.useState('')

  React.useEffect(() => {
    Promise.all(api.getUserInfo())
  .then((res) => {
    setUserName(res.name);
    setUserDescription(res.about)
    setUserAvatar(res.avatar)
  })
  .catch((err) => {
    console.log(err);
  });
  })

  return (
    <main>
      <section className="profile">
        <div className="profile__section">
          <button
            className="profile__button-edit-avatar"
            type="button"
            onClick={() => props.onEditAvatar()}
          >
            <img src={userAvatar} alt="аватар" className="profile__avatar" />
          </button>
          <div className="profile__info">
            <div className="profile__author-block">
              <h2 className="profile__author">{userName}</h2>
              <button
                type="button"
                className="profile__button-edit"
                onClick={() => props.onEditProfile()}
              ></button>
            </div>
            <p className="profile__job">{userDescription}</p>
          </div>
        </div>
        <button
          className="profile__add-button"
          type="button"
          onClick={() => props.onAddPlace()}
        ></button>
      </section>

      <section className="elements">
        <template id="elementTemplate">
          <article className="element">
            <button className="element__button-photo" type="button">
              <img className="element__photo" />
            </button>
            <div className="element__place">
              <h3 className="element__place-name"></h3>
              <button className="element__delete" type="button"></button>
              <div className="element__likes">
                <button className="element__like" type="button"></button>
                <p className="element__number-like"></p>
              </div>
            </div>
          </article>
        </template>
      </section>
    </main>
  );
}

export default Main;
