import React from "react";

function PopupWithFormCorrect({ name, title, buttonName}) {
  return (
    <div className={`popup popup_type_${name}`}>
      <div className="popup__container">
        <form className="popup__form">
          <h2 className="popup__title">{title}</h2>
          {/* <label className="popup__label">
              <input
                name="author"
                type="text"
                className="popup__input"
                placeholder="Имя автора"
                // minlength="2"
                // maxlength="40"
                // required
                // novalidate
              />
              <span className="popup__input-error popup__input-error_type_author"></span>
            </label>

            <label className="popup__label">
              <input
                name="job"
                type="text"
                className="popup__input"
                placeholder="Занятие автора"
                // minlength="2"
                // maxlength="200"
                // required
                // novalidate
              />
              <span className="popup__input-error popup__input-error_type_job"></span>
            </label> */}
            <button
              className="popup__button-submit popup__button-submit_type_save"
              type="submit"
            >
              {buttonName}
            </button>
        </form>
        <button className="popup__button-close popup__button-close_type_edit-profile"></button>
      </div>
    </div>
  );
}

export default PopupWithFormCorrect;
