import React, { useEffect } from "react";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup";
import api from "../utils/api";
import { Route, Routes } from "react-router-dom";
import CurrentUserContext from "../contexts/CurrentUserContext";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] =
    React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] =
    React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState({});

  const [currentUser, SetCurrentUser] = React.useState();
  const [cards, setCards] = React.useState([]);

  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    // Отправляем запрос в API и получаем обновлённые данные карточки
    api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    });
}


function handleCardDelete(card) {
  const isOwn = card.owner._id === currentUser._id;

  api.deleteCard(card._id, isOwn).then((deletedCard) => {
    console.log(deletedCard)
    // setCards((state) => state.filter(deletedCard => deletedCard) );
  });
}

  useEffect(() => {
    api
      .getUserInfo()
      .then((res) => {
        SetCurrentUser(res);
      })
      .catch((err) => {
        console.log(err);
      });
    api
      .getInitialCards()
      .then((res) => {
        setCards(res);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }
  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }
  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }
  function handleCardClick(card) {
    setSelectedCard(card);
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard({});
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <Header />

      <Main
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleEditAvatarClick}
        onEditAvatar={handleAddPlaceClick}
        onCardClick={handleCardClick}
        cards={cards}
        onCardLike={handleCardLike}
        onCardDelete={handleCardDelete}
      />

      <Footer />

      <PopupWithForm
        name="edit-profile"
        title="Редактировать профиль"
        buttonName="Сохранить"
        isOpen={isEditProfilePopupOpen}
        isClose={closeAllPopups}
      >
        <label className="popup__label">
          <input
            name="author"
            type="text"
            className="popup__input"
            placeholder="Имя автора"
            // minlength="2"
            // maxlength="40"
            // required
            // novalidate
          />
          <span className="popup__input-error popup__input-error_type_author"></span>
        </label>
        <label className="popup__label">
          <input
            name="job"
            type="text"
            className="popup__input"
            placeholder="Занятие автора"
            // minlength="2"
            // maxlength="200"
            // required
            // novalidate
          />
          <span className="popup__input-error popup__input-error_type_job"></span>
        </label>
      </PopupWithForm>

      <PopupWithForm
        name="update-avatar"
        title="Обновить аватар"
        buttonName="Сохранить"
        isOpen={isEditAvatarPopupOpen}
        isClose={closeAllPopups}
      >
        <label className="popup__label popup__label_one">
          <input
            type="url"
            className="popup__input"
            name="avatar"
            placeholder="Адрес фотокарточки"
            required
          />
          <span className="popup__input-error popup__input-error_type_avatar"></span>
        </label>
      </PopupWithForm>

      <PopupWithForm
        name="add-author"
        title="Новое место"
        buttonName="Создать"
        isOpen={isAddPlacePopupOpen}
        isClose={closeAllPopups}
      >
        <label className="popup__label">
          <input
            type="text"
            className="popup__input"
            name="place-name"
            placeholder="Название"
            // required
            // minlength="2"
            // maxlength="30"
            // novalidate
          />
          <span className="popup__input-error popup__input-error_type_place-name"></span>
        </label>

        <label className="popup__label">
          <input
            type="url"
            className="popup__input"
            name="photo"
            placeholder="Ссылка на картинку"
            required
          />
          <span className="popup__input-error popup__input-error_type_photo"></span>
        </label>
      </PopupWithForm>

      <PopupWithForm
        name="delete-card"
        title="Вы уверен?"
        buttonName="Да"
        isClose={closeAllPopups}
      ></PopupWithForm>

      <ImagePopup
        name="open-card"
        card={selectedCard}
        isClose={closeAllPopups}
      ></ImagePopup>
    </CurrentUserContext.Provider>
  );
}

export default App;
