import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import PopupWithFormCorrect from "./PopupWithFormCorrect";

function App() {
  return (
    <>
      <Header />
      <Main />
      <Footer />
      {/* <PopupWithForm/> */}
      <PopupWithFormCorrect
        name="edit-profile"
        title="Редактировать профиль"
        buttonName="Сохранить"
      >
        <label className="popup__label">
          <input
            name="author"
            type="text"
            className="popup__input"
            placeholder="Имя автора"
            // minlength="2"
            // maxlength="40"
            // required
            // novalidate
          />
          <span className="popup__input-error popup__input-error_type_author"></span>
        </label>
        <label className="popup__label">
          <input
            name="job"
            type="text"
            className="popup__input"
            placeholder="Занятие автора"
            // minlength="2"
            // maxlength="200"
            // required
            // novalidate
          />
          <span className="popup__input-error popup__input-error_type_job"></span>
        </label>
      </PopupWithFormCorrect>
    </>
  );
}

export default App;
