import React from "react";

function InfoToolTip(props) {
  const divider = '\u2063';
  return (
    <div className={`popup popup_type_${props.name} ${props.isOpen ? 'popup_opened' : ''} `}>
      <div className="popup__container">
        <div className="popup__box">
        <div className={`${props.auth ? 'popup__image-successed' : 'popup__image-failed'}`}></div>
        <p className="popup__text">{`${props.auth ? 'Вы успешно зарегистрировались!' : `Что-то пошло не так!${divider} Попробуйте ещё раз.`}`}</p>
        </div>
        <button className="popup__button-close" onClick={props.isClose}> </button>
      </div>
    </div>
  );
}

export default InfoToolTip;
